import 'dart:convert';

import 'package:flutter/material.dart';
import 'package:garagem_app/main.dart';
import 'package:garagem_app/mqtt.dart';
import 'package:garagem_app/widgets/appbar.dart';
import 'package:garagem_app/widgets/navbar.dart';
import 'package:garagem_app/widgets/round_rect.dart';
import 'package:garagem_app/widgets/warningInfo.dart';
import 'package:google_fonts/google_fonts.dart';
import 'package:permission_handler/permission_handler.dart';
import 'package:qr_code_scanner/qr_code_scanner.dart';


String imagemTeste = "data:image/jpeg;base64,";

class CheckCamera extends StatefulWidget{
  const CheckCamera({super.key});

  @override
  State<CheckCamera> createState() => _CheckCameraState();
}

class _CheckCameraState extends State<CheckCamera> with RouteAware{
  MQTTManager mqtt = MQTTManager();
  String? _imagemCamera;
  bool _cameraInicializado = false;

  @override
  void initState(){
    super.initState();
    mqtt.onSuccessCamera = (String payload) {
      print(payload);
      setState((){
        _imagemCamera = payload;
      });
    };
  }

  @override
  Widget build(BuildContext context){
    print(_imagemCamera);
    return Scaffold(
      appBar: const PreferredSize(
        preferredSize: Size.fromHeight(GlobalVars.appbarHeight),
        child: MyAppbar(name: "GARAGE CAMERA")
      ),
      body: Stack(
        children: <Widget>[
          Center(
            child: Column(children: <Widget>[
              SizedBox(
                width: MediaQuery.of(context).size.width - 2 * GlobalVars.gap,
                height: MediaQuery.of(context).size.width - 2 * GlobalVars.gap,
                child: (_imagemCamera != null)
                ? Image.memory(base64Decode(_imagemCamera!.split(',').last))
                : const WarningInfo(
                    type: "warning",
                    text: "Generate an Image Down Below!",
                    widgets: <Widget>[],
                  )
              ),
              RoundRect(
                onTap: () {
                  mqtt.sendMessage("", Topics.publish[2]);
                },
                margin: const EdgeInsets.only(top: GlobalVars.gap * 0.5),
                child: Text("Generate image", style: GoogleFonts.orbitron(textStyle: TxtStyles.paragraph(null, 0)))
              )
              /* Text("resultado: $result") */
            ])
          ),
          const Align(alignment: Alignment.bottomCenter, child: Navbar())
        ]
      )
    );
  }
}